window.odometerOptions = {
  format: '(,ddd).dd'
};

var scrollNumTop;
$(function(){
   $('#slider').bxSlider({
        auto:true,
        pager: true,
        displaySlideQty: 8,
        speed: 900,
        pause: 5000,
        autoHover: true,
        useCSS:false,
        easing: 'easeInOutExpo'
  });
   $(".bx-wrapper").hover(function(event) {
      $(".bx-controls-direction").show();
   }, function(event) {
       $(".bx-controls-direction").hide();
   });

   $("#Js-flash").click(lanuchFlash);
   /*修改http://xyfixbug.web.zhongchou.cn/open页面 焦点图片不自动缩放的问题*/
   if($("#Js-scrollNum").length>0)
   {
     scrollNumTop = $("#Js-scrollNum").offset().top-$(window).height();
   }
   indexAnalytics();

   //banner delay
   var banpic = $('#slider img');
   for(var i=0;i<banpic.length;i++){
     banpic.eq(i).attr('src',banpic.eq(i).data('src'));
   }

  banPicCen();
  window.onresize=wx.throttle(banPicCen,20);
});

function lanuchFlash(){
    var _this = this;
    var video = $('<iframe></iframe>').attr({
      height:'0',
      width:'0',
      src:'http://player.youku.com/embed/XNzg1MTAxMDYw',
      frameborder:'0',
      allowfullscreen:true
    });

    video.load(function() {
      $(_this).next().find('img').remove();
      video.attr({width:'500',height:'350'});
    });
    $(_this).next().append(video);
}

$(window).scroll(function () {
    var st = $(window).scrollTop();
    if(st>470){
        if(!$("#nav").hasClass('z-subnav')){
            $("#nav").hide().addClass('z-subnav').fadeIn('fast');
        }
    } else {
        $("#nav").removeClass('z-subnav');
    }
    if(st>scrollNumTop){
        $('.odometer').each(function(){
            $(this).text($(this).data('num'));
        });
    }
});

function banPicCen(){
  var windowWidth = $(window).width(),
      banPic = $('#slider').find('img');
  if(windowWidth<1600){
    banPic.css({
      "position":"absolute",
      "left":"-50%",
      "marginLeft":windowWidth/2
    });
  }else{
    banPic.css({
      "position":"static",
      "left":"inherit",
      "marginLeft":"inherit"
    });
  }
}

function showPop(){
  if(!wx.cookie("isPop")){
    $("#fenleiPop").find("img").attr("src",$("#fenleiPop").find("img").attr("wx-lz"));
    wx.pop("#fenleiPop",{y:190,attachBg:true,notAni:true});
  }
  wx.cookie("isPop",1,{expires:1});
}

function indexAnalytics(){
  $("#slider").delegate('a', 'click', function(event) {
    gaTrack('index','banner','focus',$(this).parent().index());
  });

  $(".Js-cata").delegate('a', 'click', function(event) {
    event.preventDefault();
    var link = $(this).attr("href");
    gaTrack('index','catanav',$(this).text());
    setTimeout(function(){location.href=link;},300);
  });
}

